import React, { useEffect, useState } from "react";
import "./List.scss";
import instance from "../../axios";

const baseUrl = "https://image.tmdb.org/t/p/original";

export default function List({ fetchUrl, title, isLargeRow }) {
  const [movies, setMovies] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      const respon = await instance.get(fetchUrl);
      // console.log(respon);
      setMovies(respon.data.results);
      return respon;
    };

    fetchData();
  }, [fetchUrl]);
  console.log(movies);

  const handleClick = () => {};

  return (
    <div className="list">
      <h2>{title}</h2>
      <div className="listPosters">
        {movies &&
          movies.map((movie) => (
            <img
              onClick={() => handleClick(movie)}
              className={`listPoster ${isLargeRow && "listPosterLarge"}`}
              src={`${baseUrl}${
                isLargeRow ? movie.poster_path : movie.backdrop_path
              }`}
              alt={movie.name}
              key={movie.id}
            />
          ))}
      </div>
    </div>
  );
}
